humor = "Feliz"

if humor == "Feliz":
    print("Tocar Funk")
elif humor == "Triste":    #outras condições
    print("Tocar axé")
elif humor == "Irritado":
    print("Tocar rock")
elif humor == "Da hora":
    print("Tocar trap")
else:    #nenhuma condição verdadeira (ultima alternativa)
    print("Humor não reconhecido")


